package Recursion;import java.util.Scanner;public class fib {    static int fibn(int n){        if(n==0 || n==1){            return n ;        }        return fibn(n-1)+fibn(n-2);    }    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        for(int i=0;i<n;i++) {            System.out.println(fibn(i));        }    }}